function expandCard(iconElement){const cardContainer=iconElement.closest(".resource-card");const cardContent=cardContainer.querySelector(".resource-card-content");const expandIcon=cardContainer.querySelector(".fa-solid");if(cardContent.style.display==="none"||cardContent.style.display===""){cardContent.style.display="flex";expandIcon.classList.remove("fa-circle-plus");expandIcon.classList.add("fa-circle-minus")}else{cardContent.style.display="none";expandIcon.classList.remove("fa-circle-minus");expandIcon.classList.add("fa-circle-plus")}}